&lt;pre&gt;&lt;code class=&#34;language-sh&#34;&gt;apt install vim -y
select-editor
&lt;/code&gt;&lt;/pre&gt;
&lt;p&gt;&lt;img data-src=&#34;https://cdn.jsdelivr.net/gh/zhu-jiyuan/picture_go/img/image-20220421130919271.png&#34; alt=&#34;image-20220421130919271&#34; /&gt;&lt;/p&gt;
&lt;h2 id=&#34;给vi起别名&#34;&gt;&lt;a class=&#34;anchor&#34; href=&#34;#给vi起别名&#34;&gt;#&lt;/a&gt; 给 vi 起别名&lt;/h2&gt;
&lt;pre&gt;&lt;code class=&#34;language-sh&#34;&gt;echo &amp;quot;alias vi=vim&amp;quot; &amp;gt;&amp;gt; /etc/profile
source /etc/profile
